   data_gaussMapMesh_t loadGaussMapData(
      geometry::types::enumShape_t shape_type
   )
   {
      data_gaussMapMesh_t temp_mesh;
      switch(shape_type)
      {
         case geometry::types::enumShape_t::CUBE:
         {
            temp_mesh = loadCubeGaussMesh();
            break;
         }
         case geometry::types::enumShape_t::CYLINDER:
         {
            temp_mesh = loadCylinderGaussMesh();
            break;
         }
         case geometry::types::enumShape_t::SPHERE:
         {
            temp_mesh = loadSphereGaussMesh();
            break;
         }
         case geometry::types::enumShape_t::CAPSULE:
         {
            temp_mesh = loadCapsuleGaussMesh();
            break;
         }
         default:
         {
            std::cout << "Gauss map loader has no idea what type ID: " << static_cast<int>(shape_type) << " is, so you get a cube\n";
            temp_mesh = loadCubeGaussMesh();
            break;
         }
      }

      return temp_mesh;
   }

   data_gaussMapMesh_t loadGaussMapData(data_shapeType_t shape_type)
   {
      geometry::types::enumShape_t new_shape_type;
      geometry::converters::from_pod(&shape_type, new_shape_type);
      return loadGaussMapData(new_shape_type);
   }

   geometry::types::gaussMapMesh_t loadGaussMap(
      geometry::types::enumShape_t shape_type
   )
   {
      data_gaussMapMesh_t temp_mesh = loadGaussMapData(shape_type);
      geometry::types::gaussMapMesh_t out;
      geometry::converters::from_pod(&temp_mesh, out);
      return out;
   }

   geometry::types::polygon50_t polygonFromFace(
      const geometry::types::gaussMapMesh_t & gauss_mesh_M,
      unsigned int face_id
   )
   {
      geometry::types::polygon50_t poly;
      unsigned int & num_poly_verts = poly.numVerts;
      num_poly_verts = 0;

      const geometry::types::gaussMapFace_t & face = \
         gauss_mesh_M.faces[face_id];

      if (face.numTriangles == 1)
      {
         for (int i = 0; i < 3; ++i)
         {
            unsigned int vert_id = \
               gauss_mesh_M.triangles[face.triangleStartId].vertIds[i];
            poly.verts[num_poly_verts] = gauss_mesh_M.verts[vert_id];
            ++num_poly_verts;
         }

         return poly;
      }

      std::set<unsigned int> poly_vert_ids;

      for (unsigned int i = 0; i < face.numTriangles; ++i)
      {
         unsigned int triangle_id = face.triangleStartId + i;
         const geometry::types::leanNeighborTriangle_t & triangle = \
            gauss_mesh_M.triangles[triangle_id];

         // If a triangle's neighbor ID for a given edge is not in the range
         // of triangle ID's on this face, then the vertex IDs on the edge
         // corresponding to that neighbor edge must be on the perimeter of
         // the face.
         // Neighbor index 0: vertIds [0, 1]
         // Neighbor index 1: vertIds [1, 2]
         // Neighbor index 2: vertIds [0, 2]
         for (int j = 0; j < 3; ++j)
         {
            if (
               (triangle.neighborIds[j] < static_cast<int>(face.triangleStartId)) ||
               (triangle.neighborIds[j] >= static_cast<int>(face.triangleStartId + face.numTriangles))
            )
            {
               poly_vert_ids.insert(
                  triangle.vertIds[geometry::mesh::TriangleGraphBase::edgeVerts[j][0]]
               );
               poly_vert_ids.insert(
                  triangle.vertIds[geometry::mesh::TriangleGraphBase::edgeVerts[j][1]]
               );
            }
         }
      }

      for (auto vert_id: poly_vert_ids)
      {
         if (num_poly_verts > 49)
         {
            break;
         }
         poly.verts[num_poly_verts] = gauss_mesh_M.verts[vert_id];
         ++num_poly_verts;
      }

      return poly;
   }

   geometry::types::gaussMapMesh_t gaussMap(
      const geometry::types::triangleMesh_t & convex_mesh
   )
   {
      std::vector<unsigned int> triangle_id_frontier;

      for (unsigned int i = 0; i < convex_mesh.numTriangles; ++i)
      {
         triangle_id_frontier.push_back(i);
      }

      std::vector<unsigned int> sorted_triangle_ids;
      std::vector<geometry::types::gaussMapFace_t> gauss_faces;

      while (triangle_id_frontier.size() > 0)
      {
         unsigned int num_sorted_triangle_ids = sorted_triangle_ids.size();
         // Make a new gauss face
         geometry::types::gaussMapFace_t new_gauss_face = {
            convex_mesh.triangles[triangle_id_frontier.front()].normal,
            num_sorted_triangle_ids,
            0
         };

         // Find triangles in the mesh whose normals are parallel to the normal
         // in the new gauss face.
         unsigned int & num_face_triangles = new_gauss_face.numTriangles;
         num_face_triangles = 0;
         for (auto tri_id : triangle_id_frontier)
         {
            const geometry::types::meshTriangle_t & temp_triangle = \
               convex_mesh.triangles[tri_id];

            if (temp_triangle.normal.dot(new_gauss_face.normal) >= 1.f - 1e-5f)
            {
               sorted_triangle_ids.push_back(tri_id);
               ++num_face_triangles;
            }
         }

         // Add the new gauss face to the list of faces
         gauss_faces.push_back(new_gauss_face);

         for (auto sorted_tri_id : sorted_triangle_ids)
         {
            auto old_tri_id_it = std::find(
               triangle_id_frontier.begin(),
               triangle_id_frontier.end(),
               sorted_tri_id
            );
            if (old_tri_id_it != triangle_id_frontier.end())
            {
               triangle_id_frontier.erase(old_tri_id_it);
            }
         }
      }

      TriangleGraphMesh<MAX_TRIANGLES> tgm;
      geometry::types::gaussMapMesh_t gauss_map;
      gauss_map.numFaces = 0;
      gauss_map.numTriangles = 0;
      gauss_map.numVerts = 0;

      for (auto tri_id : sorted_triangle_ids)
      {
         tgm.add_triangle(
            convex_mesh.triangles[tri_id].vertIds[0],
            convex_mesh.triangles[tri_id].vertIds[1],
            convex_mesh.triangles[tri_id].vertIds[2]
         );
      }

      if (sorted_triangle_ids.size() != convex_mesh.numTriangles)
      {
         std::cout << "num sorted triangles not equal to the number of mesh triangles\n";
         return gauss_map;
      }

      gauss_map.numTriangles = sorted_triangle_ids.size();
      for (unsigned int i = 0; i < tgm.size(); ++i)
      {
         gauss_map.triangles[i] = tgm[i];
      }

      gauss_map.numVerts = convex_mesh.numVerts;
      for (unsigned int i = 0; i < convex_mesh.numVerts; ++i)
      {
         gauss_map.verts[i] = convex_mesh.verts[i];
      }

      gauss_map.numFaces = gauss_faces.size();
      for (unsigned int i = 0; i < gauss_faces.size(); ++i)
      {
         gauss_map.faces[i] = gauss_faces[i];
      }

      return gauss_map;
   }
